mapper.select:
                                             
     	
	    select count(1)
	    from delivery_user
	    
        
	    	   
		            and id = #{id}
		       
		       
		            and wx_open_id = #{wxOpenId}
		       
	    	   
		            and name like concat(concat(#{name},"%"))
		       
	    	   
		            and phone_number like concat(concat(#{phoneNumber},"%"))
		       
	    	   
		            and login_name = #{loginName}
		       
	    	   
		            and password = #{password}
		       
	    	   
		            and equip_no = #{equipNo}
		       
	    	   
		            and idcard_no = #{idcardNo}
		       
	    	   
		            and address = #{address}
		       
		       
		            and stock_qty = #{stockQty}
		       
	    	   
		            and remark = #{remark}
		       
	    	   
		            and account_status = #{accountStatus}
		       
	    	   
		            and work_status = #{workStatus}
		       
	    	   
		            and frozen_reason = #{frozenReason}
		       
	    	   
		            and DATE_FORMAT(create_time,'%Y-%m-%d') = #{createTime}
		       
	    	   
		            and update_time = #{updateTime}

   
          select                                             
	      
	      from delivery_user
	      
	    	   
		            and id = #{id}
		       
	    	   
		            and name = #{name}
		       
		       
		            and wx_open_id = #{wxOpenId}
		       
	    	   
		            and phone_number = #{phoneNumber}
		       
	    	   
		            and login_name = #{loginName}
		       
	    	   
		            and password = #{password}
		       
	    	   
		            and equip_no = #{equipNo}
		       
	    	   
		            and idcard_no = #{idcardNo}
		       
	    	   
		            and address = #{address}
		       
		       
		            and stock_qty = #{stockQty}
		       
	    	   
		            and remark = #{remark}
		       
	    	   
		            and account_status = #{accountStatus}
		       
	    	   
		            and work_status = #{workStatus}
		       
	    	   
		            and frozen_reason = #{frozenReason}
		       
	    	   
		            and create_time = #{createTime}
		       
	    	   
		            and update_time = #{updateTime}

   
          select                                             
	      
	      from delivery_user
	      
	    	   
		            and id = #{id}
		       
		       
		            and wx_open_id = #{wxOpenId}
		       
	    	   
		            and name like concat(concat(#{name},"%"))
		       
	    	   
		            and phone_number like concat(concat(#{phoneNumber},"%"))
		       
	    	   
		            and login_name = #{loginName}
		       
	    	   
		            and password = #{password}
		       
	    	   
		            and equip_no = #{equipNo}
		       
	    	   
		            and idcard_no = #{idcardNo}
		       
	    	   
		            and address = #{address}
		       
		       
		            and stock_qty = #{stockQty}
		       
	    	   
		            and remark = #{remark}
		       
	    	   
		            and account_status = #{accountStatus}
		       
	    	   
		            and work_status = #{workStatus}
		       
	    	   
		            and frozen_reason = #{frozenReason}
		       
	    	   
	    	        and DATE_FORMAT(create_time,'%Y-%m-%d') = #{createTime}
		       
	    	   
		            and update_time = #{updateTime}
		       
	    
	    order by update_time desc
	    limit #{startIndex},#{pageSize}

   
          select                                             
	      
	      from delivery_user
	      
	    	   
		            and id = #{id}
		       
		       
		            and wx_open_id = #{wxOpenId}
		       
	    	   
		            and name = #{name}
		       
	    	   
		            and phone_number = #{phoneNumber}
		       
	    	   
