mapper.select:
select count(1) from order_info AS o left join delivery_user AS d on o.allot_delivery_id=d.id
and d.name = #{deliveryName}
and d.account_status = "10"

select d.name as delivery_name,dct.day_complete_time as day_complete_time,dat.day_accept_time as day_accept_time,mct.month_complete_time as month_complete_time,count(delivery_complete_time) as accumulated_complete_time from order_info AS o left join delivery_user AS d on o.allot_delivery_id=d.id left join (select allot_delivery_id,count(delivery_complete_time) as day_complete_time from order_info where datediff(day, delivery_complete_time ,getdate()) = 0 group by allot_delivery_id) AS dct on o.allot_delivery_id=dct.allot_delivery_id left join (select allot_delivery_id,count(delivery_order_time) as day_accept_time from order_info where datediff(day, delivery_order_time ,getdate()) = 0 group by allot_delivery_id) as dat on o.allot_delivery_id=dat.allot_delivery_id left join (select allot_delivery_id,count(delivery_complete_time) as month_complete_time from order_info where DateDiff(month, delivery_complete_time, GetDate()) = 0 group by allot_delivery_id) as mct on o.allot_delivery_id=mct.allot_delivery_id
and d.name = #{deliveryName}
and d.account_status = "10"
group by allot_delivery_id order by update_time limit #{startIndex},#{pageSize}

                                             
     	
