mapper.select:
                                             
     	
	    select count(1)
	    from order_info AS o
        left join customer_user AS c on o.customer_user_id=c.id
        left join delivery_user AS d on o.allot_delivery_id=d.id
	    
		
			
				and o.order_id like concat(concat(#{orderId},"%"))
			
			
				and o.pay_status = #{payStatus}
			
			
			    and DATE_FORMAT(o.pay_time,'%Y-%m-%d') = #{payTime}
			
			
				and o.allot_status = #{allotStatus}
			
			
				and c.name like concat(concat(#{customerName},"%"))
			
			
				and c.phone_number like concat(concat(#{customerPhoneNumber},"%"))
			
			
				and d.name like concat()
			
			
				and d.phone_number like concat(concat(#{deliveryPhoneNumber},"%"))
			
			
				and o.order_status = #{orderStatus}

                                             
     	
	    select count(1)
	    from order_info
        
		
			
				and order_id = #{orderId}
			
			
				and product_id = #{productId}
			
			
				and product_name = #{productName}oncat(#{productName},"%"))
			
			
				and amount = #{amount}
			
			
				and pay_status = #{payStatus}
			
			
				and pay_no = #{payNo}
			
			
				and pay_time = #{payTime}
			
			
				and allot_status = #{allotStatus}
			
			
				and allot_time = #{allotTime}
			
			
				and allot_delivery_id = #{allotDeliveryId}
			
			
				and delivery_order_time = #{deliveryOrderTime}
			
			
				and delivery_complete_time = #{deliveryCompleteTime}
			
			
				and customer_user_id = #{customerUserId}
			
			
				and customer_address = #{customerAddress}
			
			
				and longitude = #{longitude}
			
			
				and latitude = #{latitude}
			
			
				and order_status = #{orderStatus}
			
			
				and remark = #{remark}
			
			
				and edit_reason = #{editReason}
			
			
				and create_time = #{createTime}
			
			
				and update_time = #{updateTime}

select count(1)
from order_info

	
		and order_id like concat(concat(#{orderId},"%"))
	
	
	    and DATE_FORMAT(delivery_order_time,'%Y-%m-%d') >= #{startTime}
	
	
	    and DATE_FORMAT(delivery_order_time,'%Y-%m-%d') <= #{endTime}

SELECT o.order_id AS order_id, c. NAME AS customer_name, c.phone_number AS customer_phone_number, d. NAME AS delivery_name, d.phone_number AS delivery_phone_number, o.delivery_order_time AS delivery_order_time FROM order_info AS o LEFT JOIN customer_user AS c ON o.customer_user_id = c.id LEFT JOIN delivery_user AS d ON o.allot_delivery_id = d.id
and o.order_id like concat(concat(#{orderId},"%"))
and DATE_FORMAT(delivery_order_time,'%Y-%m-%d') >= #{startTime}
and DATE_FORMAT(delivery_order_time,'%Y-%m-%d') <= #{endTime}
order by o.update_time desc limit #{startIndex},#{pageSize}

select o.order_id AS order_id, o.remark, c.name AS customer_name, c.phone_number AS customer_phone_number, o.product_name AS product_name, p.product_desc, p.image_path AS image_path from order_info AS o left join customer_user AS c on c.id=o.customer_user_id left join product AS p on p.product_id=o.product_id
and allot_status = #{allotStatus}
and allot_delivery_id = #{allotDeliveryId}
and order_status = #{orderStatus}
order by o.update_time desc limit #{startIndex},#{pageSize}

select o.order_id AS order_id, o.product_name AS product_name, p.product_desc, p.image_path AS image_path, d.name AS delivery_name, d.phone_number AS delivery_phone_number, o.delivery_complete_time AS delivery_complete_time, c.name AS customer_name from order_info AS o left join customer_user AS c on o.customer_user_id=c.id left join delivery_user AS d on o.allot_delivery_id=d.id left join product AS p on o.product_id=p.product_id
and pay_status = #{payStatus}
and customer_user_id = #{customerUserId}
and order_status = #{orderStatus}
order by o.update_time desc limit #{startIndex},#{pageSize}

select

from order_info

	
		and order_id = #{orderId}
	
	
		and product_id = #{productId}
	
	
		and product_name = #{productName}
	
	
		and amount = #{amount}
	
	
		and pay_status = #{payStatus}
	
	
		and pay_no = #{payNo}
	
	
		and pay_time = #{payTime}
	
	
		and allot_status = #{allotStatus}
	
	
		and allot_time = #{allotTime}
	
	
		and allot_delivery_id = #{allotDeliveryId}
	
	
		and delivery_order_time = #{deliveryOrderTime}
	
	
		and delivery_complete_time = #{deliveryCompleteTime}
	
	
		and customer_user_id = #{customerUserId}
	
	
		and customer_address = #{customerAddress}
	
	
		and longitude = #{longitude}
	
	
		and latitude = #{latitude}
	
	
		and order_status = #{orderStatus}
	
	
		and remark = #{remark}
	
	
		and edit_reason = #{editReason}
	
	
		and create_time = #{createTime}
	
	
		and update_time = #{updateTime}

select o.order_id AS order_id, o.amount AS amount,
o.pay_status AS pay_status, c.name AS customer_name, c.phone_number AS customer_phone_number, o.allot_status AS allot_status, d.name AS delivery_name, d.phone_number AS delivery_phone_number, o.order_status AS order_status, o.pay_time AS pay_time, o.delivery_order_time AS delivery_order_time from order_info AS o left join customer_user AS c on o.customer_user_id=c.id left join delivery_user AS d on o.allot_delivery_id=d.id
and o.order_id like concat(concat(#{orderId},"%"))
and o.pay_status = #{payStatus}
and DATE_FORMAT(o.pay_time,'%Y-%m-%d') = #{payTime}
and o.allot_status = #{allotStatus}
and c.name like concat(concat(#{customerName},"%"))
and c.phone_number like concat(concat(#{customerPhoneNumber},"%"))
and d.name like concat(concat(#{deliveryName},"%"))
and d.phone_number like concat(concat(#{deliveryPhoneNumber},"%"))
and o.order_status = #{orderStatus}
order by o.update_time desc limit #{startIndex},#{pageSize}

select sum(o.amount) AS total_amount	
from order_info AS o
left join customer_user AS c on o.customer_user_id=c.id
left join delivery_user AS d on o.allot_delivery_id=d.id

	
		and o.order_id like concat(concat(#{orderId},"%"))
	
	
		and o.pay_status = #{payStatus}
	
	
	    and DATE_FORMAT(o.pay_time,'%Y-%m-%d') = #{payTime}
	
	
		and o.allot_status = #{allotStatus}
	
	
		and c.name like concat(concat(#{customerName},"%"))
	
	
		and c.phone_number like concat(concat(#{customerPhoneNumber},"%"))
	
	
		and d.name like concat(concat(#{deliveryName},"%"))
	
	
		and d.phone_number like concat(concat(#{deliveryPhoneNumber},"%"))
	
	
		and o.order_status = #{orderStatus}

